var util = require( './util.js' ),
	actionParams = require( './actionParams' );

/**
 * API for providing language data.
 *
 * @class LanguageInfo
 * @param {mw.Api} api
 */
function LanguageInfo( api ) {
	this.api = api;
}

LanguageInfo.prototype = {

	/**
	 * Get languageinfo API data from the local wiki, and transform it into a
	 * format usable by LanguageSearcher.
	 *
	 * @memberof LanguageInfo
	 * @instance
	 * @returns {jQuery.Deferred}
	 */
	getLanguages: function () {
		return this.api.get( actionParams( {
			meta: 'languageinfo',
			liprop: 'code|autonym|name|bcp47'
		} ) ).then( function ( resp ) {
			const filteredLanguages = [];
			// Filter out legacy languages and require an autonym.
			// If the bcp47 (https://w.wiki/Y7A) does not match the language
			// code, that is in an indication that the language code is outdated
			// and should not be used.
			Object.keys( resp.query.languageinfo ).forEach( function ( key ) {
				const language = resp.query.languageinfo[key];
				if ( ( language.code.toLowerCase() === language.bcp47.toLowerCase() ) &&
					language.autonym ) {
					filteredLanguages.push( language );
				}
			} );
			return filteredLanguages;
		}, function () {
			return util.Deferred().reject();
		} ).then( function ( filteredLanguages ) {
			return {
				languages: filteredLanguages.map( function ( data ) {
					data.url = '#';
					data.lang = data.code;
					data.langname = data.name;
					// FIXME: This isn't a "title" in the sense of a MediaWiki
					// Title, and it is rendered as a subheader in the list
					// item, so a different name would be wiser, both here and
					// in LanguageSearcher's template. Also it would arguably
					// be more intuitive for the language name (localized) to
					// appear as the main emphasized element of each language
					// list element; but instead the autonym has that role.
					// A more thorough refactoring of LanguageSearcher to allow
					// for generic header/subheader elements is left for a
					// follow-up.
					data.title = data.name;
					return data;
				} )
			};
		}, function () {
			return util.Deferred().reject();
		} );
	}
};

module.exports = LanguageInfo;
/**
 * Return the language code of the device in lowercase
 *
 * @ignore
 * @param {window.navigator} navigator
 * @return {string|undefined}
 */
function getDeviceLanguage( navigator ) {
	const lang = navigator.languages ?
		navigator.languages[ 0 ] :
		navigator.language || navigator.userLanguage ||
				navigator.browserLanguage || navigator.systemLanguage;

	return lang ? lang.toLowerCase() : undefined;
}

module.exports = getDeviceLanguage;
const
	m = require( '../moduleLoaderSingleton' ),
	getDeviceLanguage = require( './getDeviceLanguage' ),
	Overlay = require( '../Overlay' ),
	promisedView = require( '../promisedView' );

/**
 * @ignore
 * @param {LanguageInfo} languageInfo
 * @param {boolean} showSuggestedLanguages If the suggested languages section
 * should be rendered.
 * @return {jQuery.Promise} Resolves to LanguageSearcher
 */
function loadLanguageInfoSearcher( languageInfo, showSuggestedLanguages ) {
	return mw.loader.using( 'mobile.languages.structured' ).then( function () {
		return languageInfo.getLanguages();
	} ).then( function ( data ) {
		const LanguageSearcher = m.require( 'mobile.languages.structured/LanguageSearcher' );
		return new LanguageSearcher( {
			languages: data.languages,
			variants: data.variants,
			showSuggestedLanguages: showSuggestedLanguages,
			deviceLanguage: getDeviceLanguage( navigator )
		} );

	} );
}

/**
 * Factory function that returns a language info featured instance of an Overlay
 *
 * @param {LanguageInfo} languageInfo
 * @param {boolean} showSuggestedLanguages If the suggested languages section
 * should be rendered.
 * @return {Overlay}
 */
function languageInfoOverlay( languageInfo, showSuggestedLanguages ) {
	return Overlay.make(
		{
			heading: mw.msg( 'mobile-frontend-language-heading' ),
			className: 'overlay language-info-overlay'
		}, promisedView( loadLanguageInfoSearcher( languageInfo, showSuggestedLanguages ) )
	);
}

// To make knowing when async logic has resolved easier in tests
languageInfoOverlay.test = {
	loadLanguageInfoSearcher: loadLanguageInfoSearcher
};

module.exports = languageInfoOverlay;
const
	m = require( '../moduleLoaderSingleton' ),
	getDeviceLanguage = require( './getDeviceLanguage' ),
	Overlay = require( '../Overlay' ),
	promisedView = require( '../promisedView' );

/**
 * @ignore
 * @param {PageGateway} pageGateway
 * getPageLanguages API call.
 * @return {jQuery.Promise} Resolves to LanguageSearcher
 */
function loadLanguageSearcher( pageGateway ) {
	return mw.loader.using( 'mobile.languages.structured' ).then( function () {
		return pageGateway.getPageLanguages( mw.config.get( 'wgPageName' ), mw.config.get( 'wgUserLanguage' ) );
	} ).then( function ( data ) {
		const LanguageSearcher = m.require( 'mobile.languages.structured/LanguageSearcher' );

		return new LanguageSearcher( {
			languages: data.languages,
			variants: data.variants,
			showSuggestedLanguages: true,
			deviceLanguage: getDeviceLanguage( navigator )
		} );

	} );
}

/**
 * Factory function that returns a language featured instance of an Overlay
 *
 * @param {PageGateway} pageGateway
 * @return {Overlay}
 */
function languageOverlay( pageGateway ) {
	return Overlay.make(
		{
			heading: mw.msg( 'mobile-frontend-language-heading' ),
			className: 'overlay language-overlay'
		}, promisedView( loadLanguageSearcher( pageGateway ) )
	);
}

// To make knowing when async logic has resolved easier in tests
languageOverlay.test = {
	loadLanguageSearcher: loadLanguageSearcher
};

module.exports = languageOverlay;
var
	icons = require( './icons' ),
	Overlay = require( './Overlay' );

/**
 * Overlay that initially shows loading animation until
 * caller hides it with .hide()
 *
 * @return {Overlay}
 */
function loadingOverlay() {
	var overlay = new Overlay( {
		className: 'overlay overlay-loading',
		noHeader: true
	} );
	icons.spinner().$el.appendTo( overlay.$el.find( '.overlay-content' ) );
	return overlay;
}

module.exports = loadingOverlay;
var m = require( '../moduleLoaderSingleton' ),
	promisedView = require( '../promisedView' ),
	util = require( '../util' ),
	header = require( '../headers' ).header,
	icons = require( '../icons' ),
	Overlay = require( '../Overlay' );

/**
 * Produce an overlay for mediaViewer
 *
 * @param {Object} options
 * @return {Overlay}
 */
function mediaViewerOverlay( options ) {
	var overlay = Overlay.make(
		{
			headers: [
				header( '', [], icons.cancel( 'gray' ) )
			],
			className: 'overlay media-viewer'
		},
		promisedView(
			util.Promise.all( [
				mw.loader.using( 'mobile.mediaViewer' )
			] ).then( function () {
				var ImageCarousel = m.require( 'mobile.mediaViewer' ).ImageCarousel;
				return new ImageCarousel( options );
			} )
		)
	);

	return overlay;
}

module.exports = mediaViewerOverlay;
var
	moduleLoader = require( './moduleLoaderSingleton' ),
	util = require( './util' );

// Expose the entry chunk through libraryTarget and library. This allows
// arbitrary file access via ResourceLoader like
// `mfModules['mobile.startup'].moduleLoader.require('mobile.startup/LoadingOverlay')`.
module.exports = {
	moduleLoader: moduleLoader,
	mfExtend: require( './mfExtend' ),
	time: require( './time' ),
	util,
	headers: require( './headers' ),
	View: require( './View' ),
	PageGateway: require( './PageGateway' ),
	LanguageInfo: require( './LanguageInfo' ),
	Browser: require( './Browser' ),
	Button: require( './Button' ),
	Icon: require( './Icon' ),
	ReferencesGateway: require( './references/ReferencesGateway' ),
	ReferencesHtmlScraperGateway: require( './references/ReferencesHtmlScraperGateway' ),
	icons: require( './icons' ),
	Page: require( './Page' ),
	currentPage: require( './currentPage' ),
	PageHTMLParser: require( './PageHTMLParser' ),
	currentPageHTMLParser: require( './currentPageHTMLParser' ),
	Anchor: require( './Anchor' ),
	Skin: require( './Skin' ),
	OverlayManager: require( './OverlayManager' ),
	Overlay: require( './Overlay' ),
	loadingOverlay: require( './loadingOverlay' ),
	Drawer: require( './Drawer' ),
	CtaDrawer: require( './CtaDrawer' ),
	showOnPageReload: require( './showOnPageReload' ),
	// For Minerva compatibility (access deprecated)
	toast: require( './showOnPageReload' ),
	Watchstar: require( './watchstar/watchstar' ),
	eventBusSingleton: require( './eventBusSingleton' ),
	promisedView: require( './promisedView' ),
	toc: {
		// In case any old version of Minerva is cached.
		TableOfContents: () => {
			return {
				// in the unlikely event old Minerva code got loaded with new MF.
				$el: util.parseHTML( '<div>' )
			};
		}
	},
	references: require( './references/references' ),
	search: {
		SearchOverlay: require( './search/SearchOverlay' ),
		SearchGateway: require( './search/SearchGateway' )
	},
	lazyImages: {
		lazyImageLoader: require( './lazyImages/lazyImageLoader' )
	},
	languageOverlay: require( './languageOverlay/languageOverlay' ),
	languageInfoOverlay: require( './languageOverlay/languageInfoOverlay' ),
	mediaViewer: {
		overlay: require( './mediaViewer/overlay' )
	},
	amcOutreach: require( './amcOutreach/amcOutreach' ),
	Section: require( './Section' )
};

mw.mobileFrontend = moduleLoader;

// Setup a single export for new modules to fold all of the above lines into.
// One export to rule them all!
moduleLoader.define( 'mobile.startup', module.exports );
/**
 * Abstract base class
 * Gateway for retrieving references
 *
 * @class ReferencesGateway
 * @abstract
 *
 * @param {mw.Api} api
 */
function ReferencesGateway( api ) {
	this.api = api;
}

/**
 * Return the matched reference via API or DOM query
 *
 * @memberof ReferencesGateway
 * @instance
 * @param {string} id CSS selector
 * @param {Page} page to find reference for
 * @param {PageHTMLParser} pageHTMLParser
 * @return {jQuery.Promise} resolves with an Object representing reference
 *  with a `text` property
 *  The promise should be rejected with ReferenceGateway.ERROR_NOT_EXIST:
 *  if the reference is not found.
 *  If for some reason locating the reference fails return ReferenceGateway.ERROR_OTHER.
 */
ReferencesGateway.prototype.getReference = null;

/**
 * @memberof ReferencesGateway
 * @property ERROR_NOT_EXIST error code to be returned by getReference
 *  when a reference does not exist.
 */
ReferencesGateway.ERROR_NOT_EXIST = 'NOT_EXIST_ERROR';
/**
 * @memberof ReferencesGateway
 * @property ERROR_OTHER error code to be returned by getReference
 *  under any other circumstance not covered
 *  by ERROR_NOT_EXIST. It should be used if it is unclear whether a reference exists or not.
 */
ReferencesGateway.ERROR_OTHER = 'OTHER_ERROR';

module.exports = ReferencesGateway;
var ReferencesGateway = require( './ReferencesGateway' ),
	mfExtend = require( './../mfExtend' ),
	util = require( './../util' );

/**
 * Gateway for retrieving references via the content of the Page
 *
 * @class ReferencesHtmlScraperGateway
 * @extends ReferencesGateway
 * @inheritdoc
 */
function ReferencesHtmlScraperGateway() {
	ReferencesGateway.apply( this, arguments );
}

mfExtend( ReferencesHtmlScraperGateway, ReferencesGateway, {
	/**
	 * @memberof ReferencesHtmlScraperGateway
	 * @property EXTERNAL_LINK_CLASS a CSS class to place on external links
	 * in addition to the default 'external' class.
	 */
	EXTERNAL_LINK_CLASS: 'external--reference',
	/**
	 * @memberof ReferencesHtmlScraperGateway
	 * @instance
	 * @param {string} id of a DOM element in the page with '#' prefix.
	 *  can be encoded or decoded.
	 * @param {jQuery.Object} $container to scan for an element
	 * @return {jQuery.Promise} that can be used by getReference
	 */
	getReferenceFromContainer: function ( id, $container ) {
		var $el, $ol, $parent,
			result = util.Deferred();

		$el = $container.find( '#' + util.escapeSelector( id.substr( 1 ) ) );
		if ( $el.length ) {
			// This finds either the inner <ol class="mw-extended-references">, or the outer
			// <ol class="references">
			$ol = $el.closest( 'ol' );
			if ( $ol.hasClass( 'mw-extended-references' ) ) {
				$parent = $ol.parent();
			}
			// The following classes are used here:
			// * external--reference
			// * other values of EXTERNAL_LINK_CLASS in sub-classes
			( $parent || $el ).find( '.external' ).addClass( this.EXTERNAL_LINK_CLASS );
			result.resolve( {
				text: this.getReferenceHtml( $el ),
				parentText: this.getReferenceHtml( $parent )
			} );
		} else {
			result.reject( ReferencesGateway.ERROR_NOT_EXIST );
		}
		return result.promise();
	},
	/**
	 * @param {jQuery.Object|undefined} $reference
	 * @returns {string}
	 */
	getReferenceHtml: function ( $reference ) {
		return $reference ?
			$reference.find( '.mw-reference-text, .reference-text' ).first().html() :
			'';
	},
	/**
	 * @inheritdoc
	 * @memberof ReferencesHtmlScraperGateway
	 * @instance
	 * @param {string} id
	 * @param {Page} page (unused)
	 * @param {PageHTMLParser} pageHTMLParser
	 */
	getReference: function ( id, page, pageHTMLParser ) {
		try {
			id = decodeURIComponent( id );
		} catch ( e ) {
			// try id passed in - may already be decoded (T268059)
			// if it's not found it will throw an error later down the chain.
		}
		// If an id is not found it's possible the id passed needs decoding (per T188547).
		return this.getReferenceFromContainer( id, pageHTMLParser.$el.find( 'ol.references' ) );
	}
} );

module.exports = ReferencesHtmlScraperGateway;
var references,
	Drawer = require( '../Drawer' ),
	util = require( '../util' ),
	icons = require( '../icons' ),
	ReferencesGateway = require( './ReferencesGateway' ),
	Icon = require( '../Icon' );

/**
 * Create a callback for clicking references
 *
 * @param {Function} onNestedReferenceClick
 * @return {Function}
 */
function makeOnNestedReferenceClickHandler( onNestedReferenceClick ) {
	return ( ev ) => {
		const target = ev.currentTarget.querySelector( 'a' );
		if ( target ) {
			onNestedReferenceClick(
				target.getAttribute( 'href' ),
				target.textContent
			);
			// Don't hide the already shown drawer via propagation
			return false;
		}
	};
}

/**
 * Drawer for references
 *
 * @uses Icon
 * @param {Object} props
 * @param {boolean} [props.error] whether an error has occurred
 * @param {string} props.title of reference e.g [1]
 * @param {string} props.text is the HTML of the reference
 * @param {string} [props.parentText] is the HTML of the parent reference if there is one
 * @param {Function} [props.onNestedReferenceClick] callback for when a reference
 *  inside the reference is clicked.
 * @return {Drawer}
 */
function referenceDrawer( props ) {
	const errorIcon = props.error ? new Icon( {
		name: 'error',
		isSmall: true
	} ).$el : null;
	return new Drawer(
		util.extend(
			{
				showCollapseIcon: false,
				className: 'drawer position-fixed text references-drawer',
				events: {
					'click sup a': function ( ev ) {
						// Stop default scroll to hash fragment behaviour.
						ev.preventDefault();
					},
					'click sup': props.onNestedReferenceClick &&
						makeOnNestedReferenceClickHandler( props.onNestedReferenceClick )
				},
				children: [
					util.parseHTML( '<div>' )
						.addClass( 'references-drawer__header' )
						.append( [
							new Icon( {
								isSmall: true,
								name: 'reference',
								modifier: ''
							} ).$el,
							util.parseHTML( '<span>' ).addClass( 'references-drawer__title' ).text( mw.msg( 'mobile-frontend-references-citation' ) ),
							icons.cancel( 'gray', {
								isSmall: true,
								modifier: 'mw-ui-icon-element mw-ui-icon-flush-right'
							} ).$el
						] ),
					// Add .mw-parser-output so that TemplateStyles styles apply (T244510)
					util.parseHTML( '<div>' ).addClass( 'mw-parser-output' ).append( [
						errorIcon,
						props.parentText ?
							util.parseHTML( '<div>' ).html( props.parentText ) :
							'',
						util.parseHTML( '<sup>' ).text( props.title ),
						props.text ?
							util.parseHTML( '<span>' ).html( ' ' + props.text ) :
							icons.spinner().$el
					] )
				]
			},
			props
		)
	);
}

references = {
	test: {
		makeOnNestedReferenceClickHandler
	},
	referenceDrawer,
	/**
	 * Fetch and render nested reference upon click
	 *
	 * @param {string} id of the reference to be retrieved
	 * @param {Page} page to locate reference for
	 * @param {string} refNumber the number it identifies as in the page
	 * @param {PageHTMLParser} pageHTMLParser
	 * @param {Gateway} gateway
	 * @param {Object} props for referenceDrawer
	 * @param {Function} onShowNestedReference function call when a nested reference is triggered.
	 * @return {jQuery.Deferred}
	 */
	showReference: function ( id, page, refNumber, pageHTMLParser, gateway, props,
		onShowNestedReference
	) {
		return gateway.getReference( id, page, pageHTMLParser ).then( function ( reference ) {
			const drawer = referenceDrawer( util.extend( {
				title: refNumber,
				text: reference.text,
				parentText: reference.parentText,
				onNestedReferenceClick: function ( href, text ) {
					references.showReference(
						href,
						page,
						text,
						pageHTMLParser,
						gateway
					).then( ( nestedDrawer ) => {
						if ( props.onShowNestedReference ) {
							onShowNestedReference( drawer, nestedDrawer );
						} else {
							mw.log.warn( 'Please provide onShowNestedReferences parameter.' );
							document.body.appendChild( nestedDrawer.$el[ 0 ] );
							drawer.hide();
							nestedDrawer.show();
						}
					} );
				}
			}, props ) );
			return drawer;
		}, function ( err ) {
			// If non-existent reference nothing to do.
			if ( err === ReferencesGateway.ERROR_NOT_EXIST ) {
				return;
			}

			return referenceDrawer( {
				error: true,
				title: refNumber,
				text: mw.msg( 'mobile-frontend-references-citation-error' )
			} );
		} );
	}
};

module.exports = references;
var
	pageJSONParser = require( '../page/pageJSONParser' ),
	util = require( '../util' ),
	extendSearchParams = require( '../extendSearchParams' );

/**
 * @class SearchGateway
 * @uses mw.Api
 * @param {mw.Api} api
 */
function SearchGateway( api ) {
	this.api = api;
	this.searchCache = {};
	this.generator = mw.config.get( 'wgMFSearchGenerator' );
}

SearchGateway.prototype = {
	/**
	 * The namespace to search in.
	 *
	 * @memberof SearchGateway
	 * @instance
	 * @type {number}
	 */
	searchNamespace: 0,

	/**
	 * Get the data used to do the search query api call.
	 *
	 * @memberof SearchGateway
	 * @instance
	 * @param {string} query to search for
	 * @return {Object}
	 */
	getApiData: function ( query ) {
		var prefix = this.generator.prefix,
			data = extendSearchParams( 'search', {
				generator: this.generator.name
			} );

		data.redirects = '';

		data['g' + prefix + 'search'] = query;
		data['g' + prefix + 'namespace'] = this.searchNamespace;
		data['g' + prefix + 'limit'] = 15;

		// If PageImages is being used configure further.
		if ( data.pilimit ) {
			data.pilimit = 15;
			data.pithumbsize = mw.config.get( 'wgMFThumbnailSizes' ).tiny;
		}
		return data;
	},

	/**
	 * Escapes regular expression wildcards (metacharacters) by adding a \\ prefix
	 *
	 * @memberof SearchGateway
	 * @instance
	 * @param {string} str a string
	 * @return {Object} a regular expression that can be used to search for that str
	 * @private
	 */
	_createSearchRegEx: function ( str ) {
		// '\[' can be unescaped, but leave it balanced with '`]'
		// eslint-disable-next-line no-useless-escape
		str = str.replace( /[-\[\]{}()*+?.,\\^$|#\s]/g, '\\$&' );
		return new RegExp( '^(' + str + ')', 'ig' );
	},

	/**
	 * Takes a label potentially beginning with term
	 * and highlights term if it is present with strong
	 *
	 * @memberof SearchGateway
	 * @instance
	 * @param {string} label a piece of text
	 * @param {string} term a string to search for from the start
	 * @return {string} safe html string with matched terms encapsulated in strong tags
	 * @private
	 */
	_highlightSearchTerm: function ( label, term ) {
		label = util.parseHTML( '<span>' ).text( label ).html();
		term = util.parseHTML( '<span>' ).text( term ).html();

		return label.replace( this._createSearchRegEx( term ), '<strong>$1</strong>' );
	},

	/**
	 * Return data used for creating {Page} objects
	 *
	 * @memberof SearchGateway
	 * @instance
	 * @param {string} query to search for
	 * @param {Object} pageInfo from the API
	 * @return {Object} data needed to create a {Page}
	 * @private
	 */
	_getPage: function ( query, pageInfo ) {
		var page = pageJSONParser.parse( pageInfo );

		// If displaytext is set in the generator result (eg. by Wikibase),
		// use that as display title.
		// Otherwise default to the page's title.
		// FIXME: Given that displayTitle could have html in it be safe and just highlight text.
		// Note that highlightSearchTerm does full HTML escaping before highlighting.
		page.displayTitle = this._highlightSearchTerm(
			pageInfo.displaytext ? pageInfo.displaytext : page.title,
			query
		);
		page.index = pageInfo.index;

		return page;
	},

	/**
	 * Process the data returned by the api call.
	 *
	 * @memberof SearchGateway
	 * @instance
	 * @param {string} query to search for
	 * @param {Object} data from api
	 * @return {Array}
	 * @private
	 */
	_processData: function ( query, data ) {
		var self = this,
			results = [];

		if ( data.query ) {

			results = data.query.pages || {};
			results = Object.keys( results ).map( function ( id ) {
				return self._getPage( query, results[ id ] );
			} );
			// sort in order of index
			results.sort( function ( a, b ) {
				return a.index < b.index ? -1 : 1;
			} );
		}

		return results;
	},

	/**
	 * Perform a search for the given query.
	 *
	 * @memberof SearchGateway
	 * @instance
	 * @param {string} query to search for
	 * @return {jQuery.Deferred}
	 */
	search: function ( query ) {
		var xhr, request,
			self = this;

		if ( !this.isCached( query ) ) {
			xhr = this.api.get( this.getApiData( query ) );
			request = xhr
				.then( function ( data ) {
					// resolve the Deferred object
					return {
						query: query,
						results: self._processData( query, data )
					};
				}, function () {
					// reset cached result, it maybe contains no value
					self.searchCache[query] = undefined;
				} );

			// cache the result to prevent the execution of one search query twice
			// in one session
			this.searchCache[query] = request.promise( {
				abort: function () { xhr.abort(); }
			} );
		}

		return this.searchCache[query];
	},

	/**
	 * Check if the search has already been performed in given session.
	 *
	 * @memberof SearchGateway
	 * @instance
	 * @param {string} query
	 * @return {boolean}
	 */
	isCached: function ( query ) {
		return Boolean( this.searchCache[ query ] );
	}
};

module.exports = SearchGateway;
const util = require( '../util' ),
	View = require( '../View' ),
	Icon = require( '../Icon' );

/**
 * @extends View
 */
class SearchHeaderView extends View {
	/**
	 * constructor
	 *
	 * @inheritdoc
	 * @param {Object} props
	 * @param {Function} props.onInput executed every time input changes
	 * @param {string} props.placeholderMsg
	 * @param {string} props.action
	 * @param {string} [props.searchTerm] default
	 */
